package org.openpythia.utilities.sql;

import com.sun.org.apache.xpath.internal.operations.Equals;
import org.junit.Test;

import java.io.IOException;

import static org.junit.Assert.*;

public class SQLStatementTest {

    @Test
    public void normalizedSQLUnchangedTest() throws IOException {

        assertNormalizedText("SELECT * FROM dual",
                "SELECT * FROM dual");
        assertNormalizedText("SELECT * FROM dual WHERE row1 = ?",
                "SELECT * FROM dual WHERE row1 = ?");
        assertNormalizedText("SELECT * FROM dual WHERE row1 = :1",
                "SELECT * FROM dual WHERE row1 = :1");
    }

    @Test
    public void normalizedSQLReplacementTest() throws IOException {

        assertNormalizedText("SELECT 1 FROM dual",
                "SELECT ? FROM dual");
        assertNormalizedText("SELECT 'test' FROM dual",
                "SELECT ? FROM dual");
        assertNormalizedText("SELECT * FROM dual WHERE dummy IN (1, 2,3,4)",
                "SELECT * FROM dual WHERE dummy IN (?, ?,?,?)");
        assertNormalizedText("SELECT * FROM dual WHERE dummy IN ('1', '2','3','4')",
                "SELECT * FROM dual WHERE dummy IN (?, ?,?,?)");
        assertNormalizedText("SELECT * FROM dual WHERE dummy=123",
                "SELECT * FROM dual WHERE dummy=?");
        assertNormalizedText("SELECT * FROM dual WHERE dummy<123",
                "SELECT * FROM dual WHERE dummy<?");
        assertNormalizedText("SELECT * FROM dual WHERE dummy>123",
                "SELECT * FROM dual WHERE dummy>?");
        assertNormalizedText("SELECT * FROM dual WHERE dummy<>123",
                "SELECT * FROM dual WHERE dummy<>?");
    }

    private void assertNormalizedText(String sqlText, String expectedNormalizedText) {
        SQLStatement statement = new SQLStatement("SQL ID", "Parsing Schema", 0);

        statement.setSqlText(sqlText);
        assertEquals(expectedNormalizedText, statement.getNormalizedSQLText());
    }

    @Test
    public void truncatedForExcelTest() {
        SQLStatement statement = new SQLStatement("SQL ID", "Parsing Schema", 0);

        statement.setSqlText("SELECT * FROM dual");
        assertEquals("SELECT * FROM dual", statement.getSqlTextTrimmedForExcel());

        String dummyText100Chars = "12345678901234567890123456789012345678901234567890" +
                "12345678901234567890123456789012345678901234567890";
        String dummyText1000Chars =
                dummyText100Chars + dummyText100Chars + dummyText100Chars +
                        dummyText100Chars + dummyText100Chars + dummyText100Chars +
                        dummyText100Chars + dummyText100Chars + dummyText100Chars +
                        dummyText100Chars;
        String dummyText10000Chars =
                dummyText1000Chars + dummyText1000Chars + dummyText1000Chars +
                        dummyText1000Chars + dummyText1000Chars + dummyText1000Chars +
                        dummyText1000Chars + dummyText1000Chars + dummyText1000Chars +
                        dummyText1000Chars;
        String dummyText70000Chars =
                dummyText10000Chars + dummyText10000Chars + dummyText10000Chars +
                        dummyText10000Chars + dummyText10000Chars + dummyText10000Chars +
                        dummyText10000Chars;


        statement.setSqlText(dummyText70000Chars);
        // sorry, a bit long by design ;-)
        assertEquals(" ... SQL statement truncated; 37287 more characters",
                statement.getSqlTextTrimmedForExcel());

    }
}
